parameters.query:
let t = {OverTimeRange:seconds};
let w = case(t <= 86400, '7d', t <= 259200, '14d', t <= 120960, '28d', '60d');
range i from 1 to 1 step 1
| project x = w

content.query:
let startTime = {OverTimeRange:start};
let grain = {OverTimeRange:grain};
let bigWindowTimeRange = {UseComparisonTimeRangeOf};
let bigWindow = exceptions
| where timestamp >= ago(bigWindowTimeRange) and timestamp < bin(startTime, grain)
| where {Source}
| where problemId in ({ProblemFilter}) or '*' in ({ProblemFilter})
| summarize makeset(problemId, 10000);
exceptions
| where timestamp {OverTimeRange}
| where {Source}
| summarize Count = count(), Users = dcount(user_Id) by problemId
| where problemId in ({ProblemFilter}) or '*' in ({ProblemFilter})
| extend IsNew = iff(problemId !in (bigWindow), true, false)
| where "{Show}" == "['New Failure Rate (%)'], ['Existing Failure Rate (%)']" or IsNew
| order by Users desc, Count desc, problemId asc
| project Problem = iff(IsNew, strcat('ðŸ”¸ ', problemId), strcat('ðŸ”¹ ', problemId)), ['Exception Count'] = Count, ['Users Affected'] = Users
